import { useState } from "react";

const Todo = () => {
  const [err, setErr] = useState(false);
  const [text,setText] = useState("")
  const [date,setDate] = useState(0)
  var tasks = []

  const handleUpdate = async () => {
    try {
      console.log(text)
      console.log(date)
      console.log(text.length)
      console.log(date.length)
      if (text.length > 0 && date.length > 0) {
        console.log("Here1")
        let task = {
          "name": text,
          "date": date
        }
        console.log("Here2")
        tasks.push(task)
        console.log("Here3")
        console.log(task)
        console.log(tasks)
      } else {
        throw new Error("Error!")
      }
    } catch (err) {
      setErr(true);
    }
    setDate(0)
    setText("")
  }

  return (
    <div className='todo'>
      <div className='events'>
        <input type="text" name="" id="" placeholder='Type your tasks here...' onChange={e => setText(e.target.value)} value={text} />
        <input type="number" name="" id="" placeholder='Days remaining for your tasks...' onChange={e => setDate(e.target.value)} value={date} />
        <button onClick={handleUpdate}>done</button>
        {err&&<span>Please put correct text and date...</span>}
      </div>
      <div className='events'>
        {Object.entries(tasks)?.sort((a, b) => b[1].date - a[1].date).map((tasks) => (
          <div className="taskcontent" >
            <p>{tasks[1].name} {tasks[1].date}</p>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Todo
